import { useState } from 'react'
import BST from '../data-structures/BSTree'

type ReturnType<TreeType> = {
  tree: TreeType
  treeJSX: JSX.Element
  setTree: (newTreeObject: TreeType) => void
}

function useTreeState<TreeType = BST>(
  treeObject: TreeType,
): ReturnType<TreeType> {
  const [tree, setTreeInternal] = useState<TreeType>(treeObject)
  const [treeJSX, setTreeJSX] = useState<JSX.Element>(
    (treeObject as any)?.root.currentJSX,
  )
  const setTree = (newTreeObject: TreeType) => {
    setTreeInternal(newTreeObject)
    setTreeJSX(
      (newTreeObject as any)?.root?.currentJSX ||
        (newTreeObject as any)?.currentJSX,
    )
  }
  return { tree, treeJSX, setTree }
}

export default useTreeState
import { useEffect } from 'react'
import { CSS_VARIABLE_MAP, TREE_ID } from '../constants'
import { TreeStylesType } from '../types'

const useTreeStyle = (treeStyles: TreeStylesType): void => {
  const handleStyles = () => {
    if (!treeStyles) return
    for (const [style, value] of Object.entries(treeStyles)) {
      document
        .getElementById(TREE_ID)
        .style.setProperty(CSS_VARIABLE_MAP[style].variableName, value)
    }
  }
  useEffect(() => {
    handleStyles()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [treeStyles])
}
export default useTreeStyle
function styleInject(css, ref) {
  if ( ref === void 0 ) ref = {};
  var insertAt = ref.insertAt;

  if (!css || typeof document === 'undefined') { return; }

  var head = document.head || document.getElementsByTagName('head')[0];
  var style = document.createElement('style');
  style.type = 'text/css';

  if (insertAt === 'top') {
    if (head.firstChild) {
      head.insertBefore(style, head.firstChild);
    } else {
      head.appendChild(style);
    }
  } else {
    head.appendChild(style);
  }

  if (style.styleSheet) {
    style.styleSheet.cssText = css;
  } else {
    style.appendChild(document.createTextNode(css));
  }
}

export default styleInject;
import { CSS_VARIABLE_MAP } from './constants'
import { TableDataType } from './types'

export const compareArray = (array1: any[], array2: any[]): boolean => {
  if (array1.length !== array2.length) return false
  for (let i = 0; i < array1.length; i++) {
    if (array1[i] !== array2[i]) return false
  }
  return true
}

export const handleStyleArgTypes = (): Record<string, any> => {
  const argTypes = {}
  for (const [style, { defaultValue }] of Object.entries(CSS_VARIABLE_MAP)) {
    argTypes[style] = {
      control: {
        type: style.includes('Color') ? 'color' : 'text',
      },
      defaultValue,
    }
  }
  return argTypes
}

export const getTreeStylesInTableDataFormat = () => {
  const data: TableDataType = { head: [], body: [] }
  data.head = ['Property', 'Type', 'Description', 'Default']
  data.body = Object.entries(CSS_VARIABLE_MAP).map(
    ([key, { description, defaultValue }]) => [
      `<code>${key}</code>`,
      '<code>string</code>',
      description,
      `<code>${defaultValue}</code>`,
    ],
  )
  return data
}
import React, { forwardRef, useEffect, useImperativeHandle } from 'react'
import { TREE_ID } from '../constants'
import BST from '../data-structures/BSTree'
import useTreeState from '../hooks/useTreeState'
import useTreeStyle from '../hooks/useTreeStyle'
import '../styles/BinarySearchTree.css'
import {
  BinaryTreeCheckType,
  BinaryTreeRefType,
  TraversalOrderType,
  TreeStylesType,
} from '../types'
import { compareArray } from '../util'

type BSTProps = {
  data?: number[]
  treeStyles?: TreeStylesType
}

const BinarySearchTree: React.ForwardRefRenderFunction<
  BinaryTreeRefType,
  BSTProps
> = (
  { data, treeStyles }: BSTProps,
  ref: React.MutableRefObject<BinaryTreeRefType>,
) => {
  const { tree, treeJSX, setTree } = useTreeState(null)
  useTreeStyle(treeStyles)

  useImperativeHandle(ref, () => ({
    insert: (value: number) => {
      // if tree is empty
      if (!tree?.root) {
        const newTree = new BST()
        newTree.insert(value)
        setTree(newTree)
        return
      }

      tree.insert(value)
      setTree(tree)
    },
    remove: (value: number): boolean => {
      if (!tree?.root) return false

      if (!tree.search(value)) return false
      tree.remove(value)
      setTree(tree.root ? tree : null)
      return true
    },
    search: (value: number): boolean => {
      if (!tree?.root) return false
      const foundNode = tree.search(value)
      if (foundNode) setTree(tree)
      return foundNode
    },
    getData: (traversalOrder: TraversalOrderType): number[] => {
      const traversalData = []
      tree[traversalOrder](traversalData)
      return traversalData
    },
    clear: () => {
      setTree(new BST())
    },
    balance: () => {
      tree.balance()
      setTree(tree)
    },
    generateRandomTree: (countOfNodes: number) => {
      const newTree = new BST(countOfNodes)
      setTree(newTree)
    },
    checkTreeType: (): BinaryTreeCheckType[] => {
      return tree.checkBST()
    },
  }))

  const handleData = () => {
    const newTree = new BST()
    data.forEach((elem) => newTree.insert(elem))
    setTree(newTree)
  }

  useEffect(() => {
    if (data) handleData()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data])

  return (
    <div id={TREE_ID}>
      <ul>{treeJSX}</ul>
    </div>
  )
}

// returns true if data has changed
const compareBinarySearchTree = (previousPros: BSTProps, newProps: BSTProps) =>
  previousPros.data &&
  newProps.data &&
  compareArray(previousPros.data, newProps.data)

export default React.memo(forwardRef(BinarySearchTree), compareBinarySearchTree)
