import React from 'react'
import words from '../AllWords'

const ADD_HIGHLIGHT = true

class nodeTrie {
  isEnd: boolean
  children: { [key: string]: nodeTrie }
  parent: nodeTrie
  key: string
  childrenJSX: JSX.Element[]
  currentJSX: JSX.Element

  constructor(key: string) {
    this.isEnd = false
    this.children = {}
    this.parent = null
    this.key = key
    this.childrenJSX = []
    this.currentJSX = (
      <li key={this.key}>
        <div className="normal">{key ? key : 'Root'}</div>
        {this.childrenJSX.length ? <ul>{this.childrenJSX}</ul> : null}
      </li>
    )
  }

  updateJSX(isAddHighlight = false) {
    this.childrenJSX = []
    const keys = Object.keys(this.children)
    for (let i = 0; i < keys.length; i++) {
      this.childrenJSX.push(this.children[keys[i]].currentJSX)
    }

    this.currentJSX = (
      <li key={this.key}>
        <div
          className={`normal ${this.isEnd ? 'end' : ''} ${
            isAddHighlight ? 'highlight' : ''
          }`}
        >
          {this.key ? this.key : 'Root'}
        </div>
        {this.childrenJSX.length ? <ul>{this.childrenJSX}</ul> : null}
      </li>
    )

    if (this.parent) this.parent.updateJSX(isAddHighlight)
  }
}

class Trie {
  root: nodeTrie
  highlightedTill: nodeTrie

  constructor(num = null) {
    this.root = new nodeTrie(null)
    this.highlightedTill = null
    if (num) this.randomTrie(num)
  }

  insert(word: string): void {
    if (word === '') return
    word = word.trim()
    word = word.toUpperCase()
    let node = this.root
    for (let i = 0; i < word.length; i++) {
      if (!node.children[word[i]]) {
        node.children[word[i]] = new nodeTrie(word[i])
        node.children[word[i]].parent = node
      }
      node = node.children[word[i]]
      if (i === word.length - 1) node.isEnd = true
    }
    node.updateJSX()
  }

  searchWord(word: string): boolean {
    if (word === '') return false
    word = word.trim()
    word = word.toUpperCase()
    let node = this.root
    for (let i = 0; i < word.length; i++) {
      if (!node.children[word[i]]) return false
      node = node.children[word[i]]
      if (i === word.length - 1) return node.isEnd
    }
    return false
  }

  deleteWord(word: string): boolean {
    if (!this.searchWord(word)) return false
    word = word.trim()
    word = word.toUpperCase()
    let node = this.root
    for (let i = 0; i < word.length; i++) {
      node = node.children[word[i]]
    }
    node.isEnd = false

    if (Object.keys(node.children).length === 0) {
      node = node.parent
      for (let i = word.length - 1; i >= 0; i--) {
        if (node === this.root) {
          delete node.children[word[i]]
          break
        }
        if (node.isEnd) {
          delete node.children[word[i]]
          break
        }

        if (Object.keys(node.children).length > 1) {
          delete node.children[word[i]]
          break
        }
        node = node.parent
      }
    }

    node.updateJSX()
    return true
  }

  findPrefix(word: string): boolean {
    this.removeHighligt()
    if (word === '') return false
    word = word.trim()
    word = word.toUpperCase()
    let node = this.root
    for (let i = 0; i < word.length; i++) {
      if (!node.children[word[i]]) return false
      node = node.children[word[i]]
    }
    node.updateJSX(ADD_HIGHLIGHT)
    this.highlightedTill = node
    return true
  }

  findAllWords(): string[] {
    const node = this.root
    const allWords = []
    for (let i = 0; i < Object.keys(node.children).length; i++) {
      this.findAllWordsHelper(
        node.children[Object.keys(node.children)[i]],
        allWords,
      )
    }
    return allWords
  }

  findAllWordsHelper(node: nodeTrie, allWords: string[], word = ''): void {
    word += node.key
    if (node.isEnd) allWords.push(word)
    for (let i = 0; i < Object.keys(node.children).length; i++) {
      this.findAllWordsHelper(
        node.children[Object.keys(node.children)[i]],
        allWords,
        word,
      )
    }
  }

  //Inserts num random words to the trie
  randomTrie(num: number): void {
    if (num > 993) num = 993
    const allWords = words['words']
    const upper = 0
    const lower = num * 2 + 10
    const indexOfWordsAdded = new Set()
    for (let i = 0; i < num; i++) {
      let value = Math.floor(Math.random() * (upper - lower + 1)) + lower
      while (indexOfWordsAdded.has(value) || value > 993) {
        value = Math.floor(Math.random() * (upper - lower + 1)) + lower
      }
      indexOfWordsAdded.add(value)
      this.insert(allWords[value])
    }
  }

  removeHighligt(): void {
    if (this.highlightedTill) {
      this.highlightedTill.updateJSX()
      this.highlightedTill = null
    }
  }
}

export default Trie
import React, { forwardRef, useEffect, useImperativeHandle } from 'react'
import { TREE_ID } from '../constants'
import TrieDataStructure from '../data-structures/Trie'
import useTreeState from '../hooks/useTreeState'
import useTreeStyle from '../hooks/useTreeStyle'
import '../styles/BinarySearchTree.css'
import { TreeStylesType, TrieRefType } from '../types'
import { compareArray } from '../util'

type TrieProps = {
  data?: string[]
  treeStyles?: TreeStylesType
}

const Trie: React.ForwardRefRenderFunction<TrieRefType, TrieProps> = (
  { data, treeStyles }: TrieProps,
  ref: React.MutableRefObject<TrieRefType>,
) => {
  const { tree, treeJSX, setTree } = useTreeState<TrieDataStructure>(null)
  useTreeStyle(treeStyles)

  const trieFunctions: TrieRefType = {
    insert: (word: string) => {
      // if tree is empty
      if (!tree?.root) {
        const newTree = new TrieDataStructure()
        newTree.insert(word)
        setTree(newTree)
        return
      }

      tree.insert(word)
      setTree(tree)
    },
    remove: (word: string) => {
      if (!tree?.root || !word) return false
      if (tree.deleteWord(word)) {
        setTree(tree.root ? tree : null)
        return true
      }
      return false
    },
    search: (word: string) => {
      if (!tree?.root || !word) return false
      if (tree.searchWord(word)) {
        // Highlights the word in the trie
        tree.findPrefix(word)
        setTree(tree)
        return true
      }
      return false
    },
    searchPrefix: (word: string) => {
      if (!tree?.root) return false
      const isPrefixFound = tree.findPrefix(word)
      setTree(tree)
      return isPrefixFound
    },
    getAllWords: () => {
      if (!tree?.root) return []
      return tree.findAllWords()
    },
    generateRandomTrie: (wordCount: number) => {
      const newTrie = new TrieDataStructure(wordCount)
      setTree(newTrie)
    },
    removeHighlight: () => {
      tree.removeHighligt()
      setTree(tree)
    },
  }

  useImperativeHandle(ref, () => trieFunctions)

  const handleData = () => {
    const newTree = new TrieDataStructure()
    data.forEach((elem) => newTree.insert(elem))
    setTree(newTree)
  }

  useEffect(() => {
    if (data) handleData()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data])

  return (
    <div id={TREE_ID}>
      <ul>{treeJSX}</ul>
    </div>
  )
}

// returns true if data has changed
const compareTrie = (previousPros: TrieProps, newProps: TrieProps) =>
  previousPros.data &&
  newProps.data &&
  compareArray(previousPros.data, newProps.data)

export default React.memo(forwardRef(Trie), compareTrie)
import React from 'react'
import { BINARY_TREE_TYPE } from '../constants'

const LEFT = 'left'
const RIGHT = 'right'

const { BALANCED, COMPLETE, FULL, PERFECT } = BINARY_TREE_TYPE

const NULL_NODE_JSX = (
  <li className="null">
    <div>null</div>
  </li>
)

const getNormalNodeJSX = (
  value: number,
  height: number,
  leftJSX: JSX.Element,
  rightJSX: JSX.Element,
  isHighlighted = false,
) => (
  <li key={value}>
    <div className={`normal ${isHighlighted ? 'highlight' : ''}`}>
      {value} <p className="height">{height}</p>
    </div>
    <ul>
      {leftJSX} {rightJSX}
    </ul>
  </li>
)

class Node {
  value: number
  height: number
  left: Node
  right: Node
  parent: Node
  leftJSX: JSX.Element
  rightJSX: JSX.Element
  currentJSX: JSX.Element

  constructor(value: number) {
    this.value = value
    this.height = 0
    this.left = null
    this.right = null
    this.parent = null
    this.leftJSX = NULL_NODE_JSX
    this.rightJSX = NULL_NODE_JSX
    this.currentJSX = getNormalNodeJSX(
      this.value,
      this.height,
      this.leftJSX,
      this.rightJSX,
    )
  }

  //Inserting JSX of a new node
  insert(node: Node, isLeft: boolean) {
    const newJSX = node.currentJSX

    if (isLeft) {
      this.leftJSX = newJSX
    } else {
      this.rightJSX = newJSX
    }
    this.setJSX()
    this.updateRootJSX()
  }

  //Updating JSX of a node
  setJSX() {
    this.currentJSX = getNormalNodeJSX(
      this.value,
      this.height,
      this.leftJSX,
      this.rightJSX,
    )
  }

  //Updating JSX of the whole tree
  updateRootJSX() {
    if (this.parent !== null) {
      if (this.parent.left === this) this.parent.insert(this, true)
      else this.parent.insert(this, false)
    }
  }

  //Changing a child to null
  setChildToNull(isLeft: boolean) {
    if (isLeft) {
      this.leftJSX = NULL_NODE_JSX
    } else {
      this.rightJSX = NULL_NODE_JSX
    }
    this.setJSX()
    this.updateRootJSX()
  }

  //interchanging a child with a grandchild
  setChildToChildsChild(isLeftChild: boolean, isLeft: boolean) {
    if (isLeftChild) {
      if (isLeft) {
        this.leftJSX = this.left.left.currentJSX
        this.setJSX()
      } else {
        this.leftJSX = this.left.right.currentJSX
        this.setJSX()
      }
    } else {
      if (isLeft) {
        this.rightJSX = this.right.left.currentJSX
        this.setJSX()
      } else {
        this.rightJSX = this.right.right.currentJSX

        this.setJSX()
      }
    }
    this.updateRootJSX()
  }

  //Removing JSX of a deleted node
  remove(childrenCondtion?: string) {
    if (!this.parent) return
    if (!childrenCondtion) {
      if (this.parent.left === this) {
        this.parent.setChildToNull(true)
      } else {
        this.parent.setChildToNull(false)
      }
    } else if (childrenCondtion === LEFT) {
      if (this.parent.left === this) {
        this.parent.setChildToChildsChild(true, true)
      } else {
        this.parent.setChildToChildsChild(false, true)
      }
    } else {
      if (this.parent.left === this) {
        this.parent.setChildToChildsChild(true, false)
      } else {
        this.parent.setChildToChildsChild(false, false)
      }
    }
  }

  //Updates value of a node JSX
  updateValue(value: number) {
    this.value = value
    this.setJSX()
    this.updateRootJSX()
  }

  //Adds Highlight to node JSX
  addHighlight() {
    // TODO: Test
    this.currentJSX = getNormalNodeJSX(
      this.value,
      this.height,
      this.leftJSX,
      this.rightJSX,
      true,
    )

    this.updateRootJSX()
  }

  //Clears Highlight of the node JSX
  clearHighlight() {
    this.setJSX()

    this.updateRootJSX()
  }

  //Updating JSX after balancing
  balanceJSX() {
    if (!this.left && this.right) {
      this.right.balanceJSX()

      this.rightJSX = this.right.currentJSX
      this.setChildToNull(true)
    } else if (!this.right && this.left) {
      this.left.balanceJSX()

      this.leftJSX = this.left.currentJSX
      this.setChildToNull(false)
    } else if (this.right && this.left) {
      this.left.balanceJSX()
      this.right.balanceJSX()

      this.leftJSX = this.left.currentJSX
      this.rightJSX = this.right.currentJSX
      this.setJSX()
      this.updateRootJSX()
    } else {
      this.leftJSX = NULL_NODE_JSX

      this.rightJSX = NULL_NODE_JSX
      this.setJSX()
      this.updateRootJSX()
    }
  }
}

class AVL {
  root: Node
  highlightedNode: Node
  leafDepth: number
  constructor(num = 0) {
    this.root = null
    if (num) {
      this.generateRandomBST(num)
    }
    this.highlightedNode = null
    this.leafDepth = -1
  }

  //Helper functions
  rotateRight(node: Node) {
    const tempNode = node.left
    if (node.left.right) node.left.right.parent = node
    node.left = node.left.right

    //For Handling JSX
    tempNode.parent = node.parent
    node.parent = tempNode
    //For Handling JSX END

    tempNode.right = node
    tempNode.height = this.setHeight(tempNode)
    node.height = this.setHeight(node)
    return tempNode
  }

  rotateLeft(node: Node) {
    const tempNode = node.right
    if (node.right.left) node.right.left.parent = node
    node.right = node.right.left

    //For Handling JSX
    tempNode.parent = node.parent
    node.parent = tempNode
    //For Handling JSX END

    tempNode.left = node
    // tempNode.left
    tempNode.height = this.setHeight(tempNode)
    node.height = this.setHeight(node)
    return tempNode
  }

  //Sets hight of a node
  setHeight(node: Node) {
    if (node === null) return -1
    const leftHeight = node.left !== null ? node.left.height : -1
    const rightHeight = node.right !== null ? node.right.height : -1
    return Math.max(leftHeight, rightHeight) + 1
  }

  //Checks if a sub tree is balanced
  balanceCheck(node: Node) {
    if (node === null) return 0
    const leftHeight = node.left !== null ? node.left.height : -1
    const rightHeight = node.right !== null ? node.right.height : -1
    return leftHeight - rightHeight
  }

  //Balances the Tree
  balance(node: Node) {
    const balance = this.balanceCheck(node)
    if (balance > 1) {
      if (this.setHeight(node.left.left) > this.setHeight(node.left.right)) {
        node = this.rotateRight(node)
      } else {
        node.left = this.rotateLeft(node.left)

        node = this.rotateRight(node)
      }
    } else if (balance < -1) {
      if (this.setHeight(node.right.right) > this.setHeight(node.right.left)) {
        node = this.rotateLeft(node)
      } else {
        node.right = this.rotateRight(node.right)

        node = this.rotateLeft(node)
      }
    }
    if (node !== null) node.height = this.setHeight(node)
    if (node && node.right) node.right.balanceJSX()
    if (node && node.left) node.left.balanceJSX()
    return node
  }

  findMax(node = this.root) {
    if (node == null) {
      return node
    } else if (node.right == null) {
      return node
    } else {
      return this.findMax(node.right)
    }
  }

  findMin(node = this.root) {
    if (node == null) {
      return node
    } else if (node.left == null) {
      return node
    } else {
      return this.findMin(node.left)
    }
  }

  //inserting a new node
  insert(value: number) {
    this.clearHighlight()
    const newNode = new Node(value)
    if (this.root === null) this.root = newNode
    else this.root = this.insertNode(this.root, newNode)
  }

  insertNode(node: Node, newNode: Node) {
    if (newNode.value < node.value) {
      if (node.left === null) {
        node.left = newNode
        newNode.parent = node
        node.insert(newNode, true)
      } else node.left = this.insertNode(node.left, newNode)
    } else {
      if (node.right === null) {
        node.right = newNode
        newNode.parent = node
        node.insert(newNode, false)
      } else node.right = this.insertNode(node.right, newNode)
    }

    node = this.balance(node)
    return node
  }

  //Deleting node
  remove(value: number) {
    this.clearHighlight()
    this.root = this.removeNode(this.root, value)
  }

  removeNode(node: Node, value: number) {
    if (node === null || typeof node === 'undefined') return null
    else if (value < node.value) {
      node.left = this.removeNode(node.left, value)
      // return node
    } else if (value > node.value) {
      node.right = this.removeNode(node.right, value)
      // return node
    } else {
      if (node.left === null && node.right === null) {
        node.remove(null)
        node = null
        // return node
      } else if (node.left === null) {
        node.remove(RIGHT)
        node.right.parent = node.parent
        node = node.right
        // return node
      } else if (node.right === null) {
        node.remove(LEFT)
        node.left.parent = node.parent
        node = node.left
        // return node
      } else {
        const minNodeOfRight = this.findMin(node.right)
        node.updateValue(minNodeOfRight.value)

        node.right = this.removeNode(node.right, minNodeOfRight.value)
      }
    }
    node = this.balance(node)
    return node
  }

  //Tree Traversal
  preorder(list: number[], node = this.root) {
    if (node !== null) {
      list.push(node.value)
      this.preorder(list, node.left)
      this.preorder(list, node.right)
    }
  }

  inorder(list: number[], node = this.root) {
    if (node !== null) {
      this.inorder(list, node.left)
      list.push(node.value)
      this.inorder(list, node.right)
    }
  }

  postorder(list: number[], node = this.root) {
    if (node !== null) {
      this.postorder(list, node.left)
      this.postorder(list, node.right)
      list.push(node.value)
    }
  }

  //Search
  search(value: number, node = this.root) {
    this.clearHighlight()

    if (node === null) return false
    else if (node.value === value) {
      node.addHighlight()
      this.highlightedNode = node
      return true
    } else if (node.value > value) return this.search(value, node.left)
    else return this.search(value, node.right)
  }

  //Random Tree generator
  generateRandomBST(num: number) {
    const upper = 0
    const lower = num * 2 + 10
    const elements = new Set()

    for (let i = 0; i < num; i++) {
      let value = Math.floor(Math.random() * (upper - lower + 1)) + lower
      while (elements.has(value)) {
        value = Math.floor(Math.random() * (upper - lower + 1)) + lower
      }
      elements.add(value)

      this.insert(value)
    }
  }

  //BST Checker
  //Main Checker function
  checkAVL() {
    const BSTTypeList = []
    if (this.isBalanced()) {
      BSTTypeList.push(BALANCED)
    }
    if (this.isComplete()) {
      BSTTypeList.push(COMPLETE)
    }
    if (this.isPerfect()) {
      BSTTypeList.push(PERFECT)
    }
    if (this.isFull()) {
      BSTTypeList.push(FULL)
    }
    return BSTTypeList
  }

  //Subfunctions
  //balance
  isBalanced(node = this.root) {
    if (node === null) return true
    const leftH = node.left ? node.left.height : -1
    const rightH = node.right ? node.right.height : -1
    if (Math.abs(leftH - rightH) <= 1) {
      return this.isBalanced(node.left) && this.isBalanced(node.right)
    } else {
      return false
    }
  }

  //complete
  isComplete() {
    const totalNodes = this.countNodes()
    return this.isComplete2(0, totalNodes)
  }
  isComplete2(index: number, totalNodes: number, node = this.root) {
    if (node === null) return true
    else if (index >= totalNodes) return false
    else {
      let ans = this.isComplete2(index * 2 + 1, totalNodes, node.left)
      ans &= this.isComplete2(index * 2 + 2, totalNodes, node.right)
      return ans
    }
  }

  //perfect
  isPerfect() {
    if (!this.isFull() || !this.isComplete()) return false
    return this.isPerfect2()
  }

  isPerfect2(node = this.root, depth = 0) {
    if (!node) return true
    depth++
    if (!node.right && !node.left) {
      if (this.leafDepth !== -1 && this.leafDepth !== depth) return false
      this.leafDepth = depth
    }
    return (
      this.isPerfect2(node.left, depth) && this.isPerfect2(node.right, depth)
    )
  }

  //full
  isFull(node = this.root) {
    if (node === null) return true
    else if (node.left === null && node.right === null) return true
    else if (node.left !== null && node.right !== null) {
      let ans: boolean
      ans = this.isFull(node.left)
      ans &&= this.isFull(node.right)
      return ans
    } else return false
  }

  countNodes(node = this.root) {
    if (node === null) return 0
    return this.countNodes(node.left) + 1 + this.countNodes(node.right)
  }

  //Clears highlight of previously searched nodes
  clearHighlight() {
    if (this.highlightedNode) {
      this.highlightedNode.clearHighlight()
      this.highlightedNode = null
    }
  }
}

export default AVL
import React, { forwardRef, useEffect, useImperativeHandle } from 'react'
import { TREE_ID } from '../constants'
import AVL from '../data-structures/AVLTree'
import useTreeState from '../hooks/useTreeState'
import useTreeStyle from '../hooks/useTreeStyle'
import {
  SelfBalancingTreeRefType,
  BinaryTreeCheckType,
  TraversalOrderType,
  TreeStylesType,
} from '../types'
import { compareArray } from '../util'

type AVLTreeProps = {
  data?: number[]
  treeStyles?: TreeStylesType
}

const AVLTree: React.ForwardRefRenderFunction<
  SelfBalancingTreeRefType,
  AVLTreeProps
> = (
  { data, treeStyles }: AVLTreeProps,
  ref: React.MutableRefObject<SelfBalancingTreeRefType>,
) => {
  const { tree, treeJSX, setTree } = useTreeState<AVL>(null)
  useTreeStyle(treeStyles)

  useImperativeHandle(ref, () => ({
    insert: (value: number) => {
      // if tree is empty
      if (!tree?.root) {
        const newTree = new AVL()
        newTree.insert(value)
        setTree(newTree)
        return
      }

      tree.insert(value)
      setTree(tree)
    },
    remove: (value: number): boolean => {
      if (!tree?.root) return false

      if (!tree.search(value)) return false
      tree.remove(value)
      setTree(tree.root ? tree : null)
      return true
    },
    search: (value: number): boolean => {
      if (!tree?.root) return false
      const foundNode = tree.search(value)
      if (foundNode) setTree(tree)
      return foundNode
    },
    getData: (traversalOrder: TraversalOrderType): number[] => {
      const traversalData = []
      tree[traversalOrder](traversalData)
      return traversalData
    },
    clear: () => {
      setTree(new AVL())
    },
    generateRandomTree: (countOfNodes: number) => {
      const newTree = new AVL(countOfNodes)
      setTree(newTree)
    },
    checkTreeType: (): BinaryTreeCheckType[] => {
      return tree.checkAVL()
    },
  }))

  const handleData = () => {
    const newTree = new AVL()
    data.forEach((elem) => newTree.insert(elem))
    setTree(newTree)
  }

  useEffect(() => {
    if (data) handleData()
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [data])

  return (
    <div id={TREE_ID}>
      <ul>{treeJSX}</ul>
    </div>
  )
}

// returns true if data has changed
const compareAVLTree = (previousPros: AVLTreeProps, newProps: AVLTreeProps) =>
  previousPros.data &&
  newProps.data &&
  compareArray(previousPros.data, newProps.data)

export default React.memo(forwardRef(AVLTree), compareAVLTree)
import React from 'react'
import { BINARY_TREE_TYPE } from '../constants'

type Color = typeof RED | typeof BLACK

const BLACK = 0
const RED = 1
const { BALANCED, COMPLETE, FULL, PERFECT } = BINARY_TREE_TYPE

const getNormalNodeJSX = (
  value: number,
  leftJSX: JSX.Element,
  rightJSX: JSX.Element,
  color: Color,
  isHighlighted = false,
) => (
  <li key={value}>
    <div
      className={`normal ${color === RED ? 'red' : 'black'} ${
        isHighlighted ? 'highlight' : ''
      }`}
    >
      {value}
    </div>
    <ul>
      {leftJSX} {rightJSX}
    </ul>
  </li>
)

class NullNode {
  color: number
  isNull: boolean
  parent: Node
  currentJSX: JSX.Element
  value: null
  left: null
  right: null
  insert() {
    return
  }
  addHighlight() {
    return
  }
  constructor() {
    this.color = BLACK
    this.isNull = true
    this.parent = null
    this.currentJSX = (
      <li className="null">
        <div className={`null black`}>null</div>
      </li>
    )
  }
}

class Node {
  value: number
  color: Color
  right: Node | NullNode
  left: Node | NullNode
  parent: Node
  isNull: boolean
  currentJSX: JSX.Element

  constructor(value: number) {
    const newNullNode = new NullNode()
    newNullNode.parent = this
    this.value = value
    this.color = RED
    this.right = newNullNode
    this.left = newNullNode
    this.parent = null
    this.isNull = false

    this.currentJSX = (
      <li key={this.value}>
        <div className={`normal red`}>{this.value}</div>
        <ul>
          {this.left.currentJSX} {this.right.currentJSX}
        </ul>
      </li>
    )
  }

  //Updating html of a node
  setHtml() {
    this.currentJSX = getNormalNodeJSX(
      this.value,
      this.left.currentJSX,
      this.right.currentJSX,
      this.color,
    )
  }

  //Updating html of the whole tree
  updateRootHtml() {
    if (this.parent !== null) {
      this.parent.insert()
    }
  }

  //Inserting new node to tree
  insert() {
    this.setHtml()
    this.updateRootHtml()
  }

  //Adds Highlight to node html
  addHighlight() {
    this.currentJSX = getNormalNodeJSX(
      this.value,
      this.left.currentJSX,
      this.right.currentJSX,
      this.color,
      true,
    )

    this.updateRootHtml()
  }

  //Clears Highlight of the node html
  clearHighlight() {
    this.setHtml()
    this.updateRootHtml()
  }
}

class RBT {
  root: Node | NullNode
  highlighted: Node
  leafDepth: number

  constructor(num = 0) {
    const newNullNode = new NullNode()
    this.root = newNullNode
    if (num) {
      this.generateRandomBST(num)
    }
    this.highlighted = null
    this.leafDepth = -1
  }

  //Helper functions
  //Rotate right
  rotateRight(node: Node, toRecolor: boolean) {
    const parent = node.parent
    node.parent = parent.parent
    if (parent.parent !== null) {
      if (parent.parent.left === parent) {
        parent.parent.left = node
      } else {
        parent.parent.right = node
      }
    }
    const right = node.right
    node.right = parent
    parent.parent = node
    parent.left = right
    right.parent = parent
    if (toRecolor) {
      node.color = BLACK
      parent.color = RED
    }
  }

  //Rotate left
  rotateLeft(node: Node, toRecolor: boolean) {
    const parent = node.parent
    node.parent = parent.parent
    if (parent.parent !== null) {
      if (parent.parent.right === parent) {
        parent.parent.right = node
      } else {
        parent.parent.left = node
      }
    }
    const left = node.left
    node.left = parent
    parent.parent = node
    parent.right = left
    left.parent = parent
    if (toRecolor) {
      node.color = BLACK
      parent.color = RED
    }
  }

  //Find sibling
  siblingOf(node: Node) {
    if (node.parent === null) return null
    if (node.parent.left === node) {
      return node.parent.right
    } else {
      return node.parent.left
    }
  }

  //Finding min value
  findMin(node = this.root) {
    if (node.isNull) {
      return node
    } else if (node.left.isNull) {
      return node
    } else {
      return this.findMin(node.left)
    }
  }

  //Insert
  insert(value: number) {
    this.root = this.insertNode(null, this.root as Node, value)
  }

  //Insert helper function
  insertNode(parent: Node, node: Node, value: number) {
    if (node.isNull) {
      const newNode = new Node(value)
      if (parent === null) {
        newNode.color = BLACK
        newNode.insert()
        return newNode
      } else {
        newNode.parent = parent
        return newNode
      }
    }

    if (node.value === value) {
      return node
    }

    let isLeft: boolean
    if (node.value > value) {
      const left = this.insertNode(node, node.left as Node, value)
      if (left === node.parent) {
        node.insert()
        return left
      }
      node.left = left
      isLeft = true
    } else {
      const right = this.insertNode(node, node.right as Node, value)
      if (right === node.parent) {
        node.insert()
        return right
      }
      node.right = right
      isLeft = false
    }

    if (isLeft) {
      if (node.left.color === RED && node.color === RED) {
        const sibling = this.siblingOf(node)
        if (sibling.color === BLACK || sibling.isNull) {
          if (node.parent.left === node) {
            this.rotateRight(node, true)
          } else {
            this.rotateRight(node.left as Node, false)
            node = node.parent
            this.rotateLeft(node, true)
          }
        } else {
          node.color = BLACK
          sibling.color = BLACK
          if (node.parent.parent !== null) {
            node.parent.color = RED
          }
        }
      }
    } else {
      if (node.right.color === RED && node.color === RED) {
        const sibling = this.siblingOf(node)
        if (sibling.color === BLACK || sibling.isNull) {
          if (node.parent.right === node) {
            this.rotateLeft(node, true)
          } else {
            this.rotateLeft(node.right as Node, false)
            node = node.parent
            this.rotateRight(node, true)
          }
        } else {
          node.color = BLACK
          sibling.color = BLACK
          if (node.parent.parent !== null) {
            node.parent.color = RED
          }
        }
      }
    }

    if (!node.left.isNull) node.left.insert()
    if (!node.right.isNull) node.right.insert()

    return node
  }

  //Delete Node
