import React from "react";
import { MinHeap, useHeap } from 'react-tree-vis'
import { useState } from 'react'

function Minheap() {

    const { ref, insert,remove } = useHeap()

    const [insertValue, setInsertValue] = useState(0)
    const [removeValue, setRemoveValue] = useState(0)


  return (
    <>
    <div className="fl">

      <div className="fl1">
      <input
      width="50%"
        type="number"
        onChange={(elem) =>
          setInsertValue(parseInt(elem.currentTarget.value))
        }
      />
      <button onClick={() => insert(insertValue)}>Insert</button>
      </div>

        <div>
        <input
        type="number"
        onChange={(elem) => 
        setRemoveValue(parseInt(elem.currentTarget.value))}
        />

      <button onClick={() => remove(removeValue)}>Remove</button>

        </div>
    </div>


    <MinHeap ref={ref} treeStyles={{nodeFontSize :"40px"}} data={[2, 1, 3]} />
  </>
  );
}

export default Minheap;
